svg.script:
function viewerCheckURL(textid){
  var tx=document.getElementById(textid);
  var h=location.href;
  var i=h.indexOf("#");
  if(0>i)
    return;
  if(1>h.length-i)
    return;
  if(h.indexOf("%",i)>i)
    h=decodeURIComponent(h.slice(i+1))+"\n";
  else
    h=decodeURIComponent(escape(atob(h.slice(i+1).replace(/_/g,"/").replace(/-/g,"+"))))+"\n";
  if(tx.value==null)
    tx.textContent=h;
  else
    tx.value=h;
}
